rowLayout.value:
%{
	let abilities = [];	
	if (${item.tier1Unlocked}$ == true) abilities.push('${item.tier1Name}$');	
	if (${item.tier2Unlocked}$ == true) abilities.push('${item.tier2Name}$');	
	if (${item.tier3Unlocked}$ == true) abilities.push('${item.tier3Name}$');	
	if (${item.tier4Unlocked}$ == true) abilities.push('${item.tier4Name}$');	
	if (${item.tier5Unlocked}$ == true) abilities.push('${item.tier5Name}$');	

	return abilities.join(', ');
}%

hidden.value:
%{ let traits=Object.values(entity.system.props.weaponTraits).map(t=>t.trait); 
let defBonus=parseInt(entity.system.props.speed);
traits.forEach(function(item){
if (item=='Parrying') defBonus+=10;
else if (item=='Defensive') defBonus+=10;
});
return defBonus; }%

